mapper.select:
SELECT id,
videoUrl,
fileId FROM video WHERE id = #{id}

mapper.insert:
INSERT INTO video (
                videoUrl,
                fileId
    ) 
VALUES (
                #{ videoUrl },
                #{ fileId }
    )

mapper.update:
UPDATE reservation
SET 

    videoUrl = #{ videoUrl },

    fileId = #{ fileId }
WHERE id = #{id}
